/**
 * @file sketch.js
 * @author humanbydefinition (on github | instagram)
 * @date 07/2024
 *
 * @repository https://github.com/humanbydefinition/p5js-brightness-and-edge-ascii-renderer
 *
 * @brief This is the main entry point for the p5.js brightness and edge ascii renderer using glsl shaders.
 * 
 * It includes the setup and draw functions, which are the main entry points for p5.js. 
 * It also includes the logic for resizing the window, and preloading necessary assets.
 * 
 * A Sobel edge detection shader that calculates the gradient using convolution with Sobel kernels. 
 * It detects edges by computing the gradient magnitude and direction, highlighting edges based on their orientation.
 * 
 * A sample shader is used to convert the sobel edge detection output to a grid of single colored rectangles, which
 * is later used to render the ascii characters for the edges.
 * 
 * The ascii brightness and edge buffer use the same shader in different modes. 
 * The brightness buffer uses the shader to convert the brightness of the scene to ASCII characters.
 * The edge buffer uses the shader to convert the edges of the scene to ASCII characters, overlaying them on the brightness buffer.
 * 
 * The project also includes a UI for adjusting parameters during run-time. This is done using the Tweakpane library.
 * 
 * The project also supports recording the output to a video file. This is done using the p5.Capture library.
 */

// Default parameters for the sketch on startup
const PARAMS = {
	desiredFrameRate: 60,

	// base64 encoded font data
	asciiFontData: "data:text/javascript;base64,",

	asciiFontSize: 16,

	asciiBrightnessCharacterSet: " .:+*#",
	asciiBrightnessInvertCharacters: false,
	asciiBrightnessCharacterColorMode: 0, // 0: sampled, 1: fixed
	asciiBrightnessBackgroundColorMode: 1,
	asciiBrightnessCharacterColor: "#ffffff",
	asciiBrightnessBackgroundColor: "#000000",

	asciiEdgesCharacterSet: "-/|\\-/|\\", // always should be 8 characters
	asciiEdgesInvertCharacters: false,
	asciiEdgesCharacterColorMode: 0, // 0: sampled, 1: fixed
	asciiEdgesBackgroundColorMode: 1,
	asciiEdgesCharacterColor: "#ffffff",
	asciiEdgesBackgroundColor: "#000000",

	sobelThreshold: 0.5, // Threshold for the sobel filter
	sobelSampleThreshold: 1, // Threshold for the edge sampling (How many pixels need to be non-black in a grid cell to be considered as an edge?)

	selectedDisplayFramebuffer: "ascii + edges", // Which framebuffer to display on the canvas

	recordingType: "webm",
	recordingActive: false,
	recordingElapsedTime: "00:00:00.0",

	fontFileInput: "",
	fontObject: ""
};

// Set the default options for P5.Capture
P5Capture.setDefaultOptions({
	disableUi: false,
	quality: 1,
	framerate: 60,
	verbose: true,
});

// Other global objects, which are used across this project, just like PARAMS
let asciiFont; // The font object, which is used to create the character sets

let grid; // The grid object, which is used for grid measurements

let edgesCharacterSet; // The character set object, which is used for the edge ascii shader
let brightnessCharacterSet; // The character set object, which is used for the brightness ascii shader

let sketchFramebuffer; // All the framebuffers, which are for the rendering pipeline
let sobelFramebuffer;
let sobelSampleFramebuffer;
let asciiBrightnessFramebuffer;
let asciiEdgesFramebuffer;

let displayFramebuffer; // The framebuffer, which is displayed on the canvas (It can be any of the framebuffers above)

let sobelShader; // All the shaders, which are used in the rendering pipeline
let sampleShader;
let asciiShader;

let overlay; // The tweakpane overlay object, which allows modification of certain parameters during runtime

// The capturer object, used for recording the canvas using p5.Capture
let capturer;

/**
 * Preloads the necessary assets for the sketch.
 * @function preload
 * @global
 */
function preload() {
	asciiFont = loadFont(PARAMS.asciiFontData);

	sobelShader = loadShader("shaders/vert/shader.vert", "shaders/frag/sobel.frag");
	sampleShader = loadShader("shaders/vert/shader.vert", "shaders/frag/sample.frag");
	asciiShader = loadShader("shaders/vert/shader.vert", "shaders/frag/ascii.frag");
}

/**
 * Sets up the p5.js sketch.
 * @function setup
 * @global
 */
function setup() {
	createCanvas(windowWidth, windowHeight, WEBGL);

	edgesCharacterSet = new CharacterSet({ font: asciiFont, characters: PARAMS.asciiEdgesCharacterSet, fontSize: PARAMS.asciiFontSize });
	brightnessCharacterSet = new CharacterSet({ font: asciiFont, characters: PARAMS.asciiBrightnessCharacterSet, fontSize: PARAMS.asciiFontSize });

	grid = new Grid({ cellWidth: edgesCharacterSet.maxGlyphDimensions.width, cellHeight: edgesCharacterSet.maxGlyphDimensions.height });

	sketchFramebuffer = createFramebuffer({ format: FLOAT, width: grid.width, height: grid.height });
	sobelFramebuffer = createFramebuffer({ format: FLOAT, width: grid.width, height: grid.height });
	sobelSampleFramebuffer = createFramebuffer({ format: FLOAT, width: grid.width, height: grid.height });
	asciiBrightnessFramebuffer = createFramebuffer({ format: FLOAT, width: grid.width, height: grid.height });
	asciiEdgesFramebuffer = createFramebuffer({ format: FLOAT, width: grid.width, height: grid.height });

	displayFramebuffer = asciiEdgesFramebuffer; // Set the canvas buffer to the ascii framebuffer by default

	overlay = new Overlay(); // Initialize the overlay and the capturer
}

/**
 * The main drawing function that is called continuously by p5.js.
 * @function draw
 * @global
 */
function draw() {

	overlay.fps_graph.begin(); // Wrap the draw function with the FPS graph to determine the frame rate

	/**
	* Your creative code goes here to replace the following code, drawing to the sketch buffer.
	* Currently, the code draws a Tim Rodenbroeker-esque rotating 3D box to the sketch buffer.
	* Check out his courses on creative coding at https://timrodenbroeker.de/ (no affiliation, I just enjoyed his courses)
	*/
	sketchFramebuffer.begin();
	background(0);
	fill(255);

	push();
	rotateX(radians(frameCount * 3));
	rotateZ(radians(frameCount));
	directionalLight(255, 255, 255, 0, 0, -1);
	box(800, 100, 100);
	pop();
	sketchFramebuffer.end();

	sobelFramebuffer.begin(); // Apply the sobel shader to the sketch buffer and store the result in the sobel buffer
	shader(sobelShader);
	sobelShader.setUniform('u_texture', sketchFramebuffer);
	sobelShader.setUniform('u_textureSize', [windowWidth, windowHeight]);
	sobelShader.setUniform('u_threshold', PARAMS.sobelThreshold);
	rect(0, 0, windowWidth, windowHeight);
	sobelFramebuffer.end();

	sobelSampleFramebuffer.begin(); // Apply the sample shader to the sobel buffer and store the result in the sample buffer
	shader(sampleShader);
	sampleShader.setUniform('u_image', sobelFramebuffer);
	sampleShader.setUniform('u_gridCellDimensions', [grid.cols, grid.rows]);
	sampleShader.setUniform('u_threshold', PARAMS.sobelSampleThreshold);
	rect(0, 0, windowWidth, windowHeight);
	sobelSampleFramebuffer.end();

	asciiBrightnessFramebuffer.begin(); // Apply the ascii brightness shader to the sketch buffer and store the result in the ascii brightness buffer
	shader(asciiShader);
	asciiShader.setUniform('u_characterTexture', brightnessCharacterSet.texture);
	asciiShader.setUniform('u_charsetCols', brightnessCharacterSet.charsetCols);
	asciiShader.setUniform('u_charsetRows', brightnessCharacterSet.charsetRows);
	asciiShader.setUniform('u_totalChars', brightnessCharacterSet.characters.length);
	asciiShader.setUniform('u_sketchTexture', sketchFramebuffer); // Used for coloring the ascii characters and determining the character based on brightness
	asciiShader.setUniform('u_gridCellDimensions', [grid.cols, grid.rows]);
	asciiShader.setUniform('u_characterColor', ColorTranslator.hexToShaderColor(PARAMS.asciiBrightnessCharacterColor));
	asciiShader.setUniform('u_characterColorMode', PARAMS.asciiBrightnessCharacterColorMode);
	asciiShader.setUniform('u_backgroundColor', ColorTranslator.hexToShaderColor(PARAMS.asciiBrightnessBackgroundColor));
	asciiShader.setUniform('u_backgroundColorMode', PARAMS.asciiBrightnessBackgroundColorMode);
	asciiShader.setUniform('u_invertMode', PARAMS.asciiBrightnessInvertCharacters);
	asciiShader.setUniform('u_renderMode', 0); // 0: render ascii brightness, 1: render ascii edges
	rect(0, 0, windowWidth, windowHeight);
	asciiBrightnessFramebuffer.end();

	asciiEdgesFramebuffer.begin(); // Apply the ascii edges shader to the sample buffer and store the result in the ascii edges buffer
	shader(asciiShader);
	asciiShader.setUniform('u_characterTexture', edgesCharacterSet.texture);
	asciiShader.setUniform('u_charsetCols', edgesCharacterSet.charsetCols);
	asciiShader.setUniform('u_charsetRows', edgesCharacterSet.charsetRows);
	asciiShader.setUniform('u_totalChars', edgesCharacterSet.characters.length);
	asciiShader.setUniform('u_edgesTexture', sobelSampleFramebuffer); // Used for detecting the edges to apply the edge characters to
	asciiShader.setUniform('u_sketchTexture', sketchFramebuffer); // Used for coloring the edge characters
	asciiShader.setUniform('u_asciiBrightnessTexture', asciiBrightnessFramebuffer); // If no edge is present, apply the pixel from the brightness ascii buffer
	asciiShader.setUniform('u_gridCellDimensions', [grid.cols, grid.rows]);
	asciiShader.setUniform('u_characterColor', ColorTranslator.hexToShaderColor(PARAMS.asciiEdgesCharacterColor));
	asciiShader.setUniform('u_characterColorMode', PARAMS.asciiEdgesCharacterColorMode);
	asciiShader.setUniform('u_backgroundColor', ColorTranslator.hexToShaderColor(PARAMS.asciiEdgesBackgroundColor));
	asciiShader.setUniform('u_backgroundColorMode', PARAMS.asciiEdgesBackgroundColorMode);
	asciiShader.setUniform('u_invertMode', PARAMS.asciiEdgesInvertCharacters);
	asciiShader.setUniform('u_renderMode', 1); // 0: render ascii brightness, 1: render ascii edges
	rect(0, 0, windowWidth, windowHeight);
	asciiEdgesFramebuffer.end();

	// Draw the selected framebuffer to the canvas
	background(0); // Clear the canvas (covers the outer window edges that might not be covered due to the grid size being smaller than the window maybe)
	image(displayFramebuffer, (-windowWidth / 2) + grid.offsetX, (-windowHeight / 2) + grid.offsetY);

	if (PARAMS.recordingActive) { // If recording is active, update the elapsed time
		const captureTimerElement = document.querySelector('.p5c-counter');  // Get the capture timer element
		PARAMS.recordingElapsedTime = captureTimerElement.innerText;
	} else {
		PARAMS.recordingElapsedTime = '0:00:00.0'; // Reset the elapsed time when not recording
	}

	overlay.fps_graph.end(); // End the FPS graph
}

/**
 * Resizes the canvas when the window is resized.
 * @function windowResized
 * @global
 */
function windowResized() {
	resizeCanvas(windowWidth, windowHeight);
	grid.reset();
	resizeFramebuffers();
}

/**
 * Resizes the framebuffers when the grid dimensions change.
 * @function resizeFramebuffers
 * @global
 */
function resizeFramebuffers() {
	sketchFramebuffer.resize(grid.width, grid.height);
	sobelFramebuffer.resize(grid.width, grid.height);
	sobelSampleFramebuffer.resize(grid.width, grid.height);
	asciiBrightnessFramebuffer.resize(grid.width, grid.height);
	asciiEdgesFramebuffer.resize(grid.width, grid.height);
}

/**
 * Updates the display framebuffer based on the selected display framebuffer parameter.
 * @function updateDisplayFramebuffer
 * @global
 */
function updateDisplayFramebuffer(selectedFramebufferId) {
	PARAMS.selectedDisplayFramebuffer = selectedFramebufferId;

	if (PARAMS.selectedDisplayFramebuffer === "sketch") {
		displayFramebuffer = sketchFramebuffer;
	} else if (PARAMS.selectedDisplayFramebuffer === "sobel") {
		displayFramebuffer = sobelFramebuffer;
	} else if (PARAMS.selectedDisplayFramebuffer === "sobel sample") {
		displayFramebuffer = sobelSampleFramebuffer;
	} else if (PARAMS.selectedDisplayFramebuffer === "ascii") {
		displayFramebuffer = asciiBrightnessFramebuffer;
	} else if (PARAMS.selectedDisplayFramebuffer === "ascii + edges") {
		displayFramebuffer = asciiEdgesFramebuffer;
	}
}
